mapper.insert:
INSERT INTO TBL_MEETING
(
    TITLE
    , LOCATION_NAME
    , LOCATION_LATITUDE
    , LOCATION_LONGITUDE
    , INTRODUCTION
    , PAYMENT_METHOD
    , MEETING_TYPE
    , SCHEDULED_DATE
    , EXIT_RESTRICTION_TIME
    , RECRUITMENT_NUMBER
    , GENDER
    , ELIGIBLE_AGE
    , CREATED_DATE
    , PARTICIPATION_METHOD
    , STATUS
    , END_DATE
    , HOST_MEMBER_ID
)
VALUES
(
    #{ title }
    , #{ locationName }
    , #{ locationLatitude }
    , #{ locationLongitude }
    , #{ introduction }
    , #{ paymentMethod }
    , #{ meetingType }
    , #{ scheduledDate }
    , #{ exitRestrictionTime }
    , #{ recruitmentNumber }
    , #{ gender }
    , #{ eligibleAge }
    , #{ createdDate }
    , #{ participationMethod }
    , #{ status }
    , #{ endDate }
    , #{ hostMemberId }
)

mapper.select:
SELECT A.MEETING_ID , A.TITLE , A.LOCATION_NAME , A.LOCATION_LATITUDE , A.LOCATION_LONGITUDE , A.INTRODUCTION , A.PAYMENT_METHOD , A.MEETING_TYPE , C.INTERESTS , A.SCHEDULED_DATE , A.EXIT_RESTRICTION_TIME , A.RECRUITMENT_NUMBER , A.GENDER , A.ELIGIBLE_AGE , A.CREATED_DATE , A.PARTICIPATION_METHOD , A.STATUS , A.END_DATE , A.HOST_MEMBER_ID FROM TBL_MEETING AS A LEFT JOIN TBL_MEETING_INTEREST_RELATION AS B ON A.MEETING_ID = B.MEETING_ID LEFT JOIN TBL_MEETING_INTEREST_CATEGORY AS C ON B.INTERESTS = C.INTERESTS
A.TITLE LIKE CONCAT('%', #{ word }, '%')
AND C.INTERESTS IN
#{ condition }
AND A.MEETING_TYPE = #{ typeCondition }
AND A.STATUS = 'Open'
